import React, { useState, useEffect } from "react";
import Quadrado from "./Quadrado";
import "./tabuleiro.css"; // Arquivo para estilos

function Tabuleiro() {
  const [quadrados, setQuadrados] = useState(Array(9).fill(null));
  const [xIsNext, setXIsNext] = useState(true);
  const [posicoesVencedoras, setPosicoesVencedoras] = useState([]);
  const [vencedor, setVencedor] = useState(null);

  function handleClick(index) {
    if (quadrados[index] || vencedor) return;

    const novosQuadrados = quadrados.slice();
    novosQuadrados[index] = xIsNext ? "X" : "O";
    setQuadrados(novosQuadrados);
    setXIsNext(!xIsNext);

    const resultado = calcularVencedor(novosQuadrados);
    if (resultado) {
      setVencedor(resultado.vencedor);
      setPosicoesVencedoras(resultado.posicoes);
    }
  }

  function reiniciarJogo() {
    setQuadrados(Array(9).fill(null));
    setXIsNext(true);
    setPosicoesVencedoras([]);
    setVencedor(null);
  }

  function renderizarQuadrado(index) {
    const isVencedor = posicoesVencedoras.includes(index);
    return (
      <Quadrado
        value={quadrados[index]}
        onClick={() => handleClick(index)}
        isVencedor={isVencedor}
      />
    );
  }

  const status = vencedor
    ? `Vencedor: ${vencedor}`
    : quadrados.every(Boolean)
    ? "Empate!"
    : `Jogador: ${xIsNext ? "X" : "O"}`;

  return (
    <div className="tabuleiro-container">
      <h1>X-bola</h1>
      <div className="status">{status}</div>
      <div className="tabuleiro">
        <div style={{ display: "flex" }}>
          {renderizarQuadrado(0)}
          {renderizarQuadrado(1)}
          {renderizarQuadrado(2)}
        </div>
        <div style={{ display: "flex" }}>
          {renderizarQuadrado(3)}
          {renderizarQuadrado(4)}
          {renderizarQuadrado(5)}
        </div>
        <div style={{ display: "flex" }}>
          {renderizarQuadrado(6)}
          {renderizarQuadrado(7)}
          {renderizarQuadrado(8)}
        </div>
        {/* Linha de vit√≥ria ajustada */}
{vencedor && posicoesVencedoras.length === 3 && (
  <div
    className="linha-vitoria"
    style={calcularEstiloRisco(posicoesVencedoras)}
  ></div>
)}

      </div>
      <button className="reiniciar" onClick={reiniciarJogo}>
        Reiniciar
      </button>
    </div>
  );
}

function calcularVencedor(quadrados) {
  const linhas = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < linhas.length; i++) {
    const [a, b, c] = linhas[i];
    if (quadrados[a] && quadrados[a] === quadrados[b] && quadrados[a] === quadrados[c]) {
      return { vencedor: quadrados[a], posicoes: [a, b, c] };
    }
  }
  return null;
}

export default Tabuleiro;
