import React, { useState, useEffect } from "react";
import Quadrado from "./Quadrado";
import "./tabuleiro.css";

function Tabuleiro() {
  const [quadrados, setQuadrados] = useState(Array(9).fill(null));
  const [xIsNext, setXIsNext] = useState(true);
  const [linhaVencedora, setLinhaVencedora] = useState(null);

  // Calcula o vencedor ou verifica empate após cada jogada
  useEffect(() => {
    const resultado = calcularVencedor(quadrados);
    if (resultado) {
      setLinhaVencedora(resultado.linhaVencedora);
    } else if (!quadrados.includes(null)) {
      setTimeout(reiniciarJogo, 2000); // Empate: Reinicia automaticamente
    }
  }, [quadrados]);

  function handleClick(index) {
    if (quadrados[index] || linhaVencedora !== null) return;

    const novosQuadrados = quadrados.slice();
    novosQuadrados[index] = xIsNext ? "X" : "O";
    setQuadrados(novosQuadrados);
    setXIsNext(!xIsNext);

    const resultado = calcularVencedor(novosQuadrados);
    if (resultado) {
      setTimeout(reiniciarJogo, 2000); // Vitória: Reinicia automaticamente
    }
  }

  function renderizarQuadrado(index) {
    return (
      <Quadrado
        value={quadrados[index]}
        onClick={() => handleClick(index)}
      />
    );
  }

  function reiniciarJogo() {
    setQuadrados(Array(9).fill(null));
    setXIsNext(true);
    setLinhaVencedora(null);
  }

  const status = linhaVencedora
    ? `Vencedor: ${quadrados[linhaVencedora[0]]}`
    : quadrados.includes(null)
    ? `Próximo Jogador: ${xIsNext ? "X" : "O"}`
    : "Empate! Reiniciando...";

  return (
    <div className="tabuleiro">
      <div>{status}</div>
      <div className="linha">
        {renderizarQuadrado(0)}
        {renderizarQuadrado(1)}
        {renderizarQuadrado(2)}
      </div>
      <div className="linha">
        {renderizarQuadrado(3)}
        {renderizarQuadrado(4)}
        {renderizarQuadrado(5)}
      </div>
      <div className="linha">
        {renderizarQuadrado(6)}
        {renderizarQuadrado(7)}
        {renderizarQuadrado(8)}
      </div>
      {linhaVencedora !== null && (
        <div className={`risco linha-${linhaVencedora}`}></div>
      )}
      <button onClick={reiniciarJogo} className="reiniciar-botao">
        Reiniciar Jogo
      </button>
    </div>
  );
}

export default Tabuleiro;

// Função para calcular o vencedor
function calcularVencedor(quadrados) {
  const linhas = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < linhas.length; i++) {
    const [a, b, c] = linhas[i];
    if (quadrados[a] && quadrados[a] === quadrados[b] && quadrados[a] === quadrados[c]) {
      return { vencedor: quadrados[a], linhaVencedora: i, posicoes: [a, b, c] };
    }
  }
  return null;
}
